import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Set the base URL for Axios requests to your backend
axios.defaults.baseURL = 'http://localhost:3002';

// Set default headers for Axios requests
axios.defaults.headers.common = {
  'Content-Type': 'application/json',
  // Add other essential headers here if necessary
};

// Intercept the request to log headers before sending (for debugging)
axios.interceptors.request.use(request => {
  console.log('Starting Request', request);
  return request;
});

const Rentals = () => {
  const [rentals, setRentals] = useState([]);
  const [form, setForm] = useState({
    rental_id: '',
    start_date: '',
    due_date: '',
    date_returned: '',
    payment_id: '',
    customer_id: '',
    movie_id: ''
  });
  const [isEditing, setIsEditing] = useState(false);

  // Fetch rentals data from the backend
  useEffect(() => {
    fetchRentals();
  }, []);

  const fetchRentals = async () => {
    try {
      const response = await axios.get('/rentals');
      setRentals(response.data);
    } catch (error) {
      console.error('There was an error fetching the rentals!', error);
    }
  };

  // Handle form input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  // Handle form submit for adding/updating rental
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEditing) {
        await axios.put(`/rentals/${form.rental_id}`, form);
      } else {
        await axios.post('/rentals', form);
      }
      setForm({
        rental_id: '',
        start_date: '',
        due_date: '',
        date_returned: '',
        payment_id: '',
        customer_id: '',
        movie_id: ''
      });
      setIsEditing(false);
      fetchRentals(); // Refresh the list after submission
    } catch (error) {
      console.error('There was an error submitting the rental!', error);
    }
  };

  // Handle edit button click
  const handleEdit = (rental) => {
    setForm(rental);
    setIsEditing(true);
  };

  // Handle delete button click
  const handleDelete = async (rental_id) => {
    try {
      await axios.delete(`/rentals/${rental_id}`);
      fetchRentals(); // Refresh the list after deletion
    } catch (error) {
      console.error('There was an error deleting the rental!', error);
    }
  };

  return (
    <div>
      <h1>Rentals</h1>

      <nav>
        <a href="memberships.html">Memberships</a> | <a href="customers.html">Customers</a> | 
        <a href="payments.html">Payments</a> | <a href="movies.html">Movies</a> | 
        <a href="rentals.html">Rentals</a>
      </nav>

      <div id="browse">
        <p><strong>Browse Rentals</strong></p>
        <table border="1" cellPadding="5">
          <thead>
            <tr>
              <th>Edit</th>
              <th>Delete</th>
              <th>rental_id</th>
              <th>start_date</th>
              <th>due_date</th>
              <th>date_returned</th>
              <th>payment_id</th>
              <th>customer_id</th>
              <th>movie_id</th>
            </tr>
          </thead>
          <tbody>
            {rentals.map((rental) => (
              <tr key={rental.rental_id}>
                <td><button onClick={() => handleEdit(rental)}>Edit</button></td>
                <td><button onClick={() => handleDelete(rental.rental_id)}>Delete</button></td>
                <td>{rental.rental_id}</td>
                <td>{rental.start_date}</td>
                <td>{rental.due_date}</td>
                <td>{rental.date_returned}</td>
                <td>{rental.payment_id}</td>
                <td>{rental.customer_id}</td>
                <td>{rental.movie_id}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div id="insert" style={{ display: 'block' }}>
        <form onSubmit={handleSubmit}>
          <legend><strong>{isEditing ? 'Update Rental' : 'Add Rental'}</strong></legend>
          <fieldset className="fields">
            {isEditing && (
              <div>
                <label>Rental ID:</label>
                <input type="text" name="rental_id" value={form.rental_id} disabled />
              </div>
            )}
            <label> Start Date </label> <input type="date" name="start_date" value={form.start_date} onChange={handleInputChange} />
            <label> Due Date </label> <input type="date" name="due_date" value={form.due_date} onChange={handleInputChange} />
            <label> Date Returned </label> <input type="date" name="date_returned" value={form.date_returned} onChange={handleInputChange} />
            <label> Payment ID </label> <input type="text" name="payment_id" value={form.payment_id} onChange={handleInputChange} />
            <label> Customer ID </label> <input type="text" name="customer_id" value={form.customer_id} onChange={handleInputChange} />
            <label> Movie ID </label> <input type="text" name="movie_id" value={form.movie_id} onChange={handleInputChange} />
          </fieldset>
          <input className="btn" type="submit" value={isEditing ? 'Save Update' : 'Add Rental'} />
          <input className="btn" type="button" value="Cancel" onClick={() => { 
            setIsEditing(false); 
            setForm({
              rental_id: '',
              start_date: '',
              due_date: '',
              date_returned: '',
              payment_id: '',
              customer_id: '',
              movie_id: ''
            }); 
          }} />
        </form>
      </div>
    </div>
  );
}

export default Rentals;
