import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Movies = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    axios.get('/api/movies')
      .then(response => {
        setMovies(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the movies data!", error);
      });
  }, []);

  const handleDelete = (id) => {
    axios.delete(`/api/movies/${id}`)
      .then(response => {
        setMovies(movies.filter(movie => movie.id !== id));
      })
      .catch(error => {
        console.error("There was an error deleting the movie!", error);
      });
  };

  return (
    <div style={styles.container}>
      <h1>Movies List</h1>
      <Link to="/create-movie" style={styles.createButton}>Create +</Link>
      <table style={styles.table}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Genre</th>
            <th>Release Date</th>
            <th>Price</th>
            <th>Amount</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {movies.map((movie) => (
            <tr key={movie.id}>
              <td>{movie.id}</td>
              <td>{movie.title}</td>
              <td>{movie.genre}</td>
              <td>{movie.releaseDate}</td>
              <td>{movie.price}</td>
              <td>{movie.amount}</td>
              <td>
                <Link to={`/movies/${movie.id}`} style={styles.actionButton}>Read</Link>
                <Link to={`/movies/edit/${movie.id}`} style={styles.actionButton}>Edit</Link>
                <button style={styles.deleteButton} onClick={() => handleDelete(movie.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
  },
  createButton: {
    backgroundColor: 'green',
    color: 'white',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    marginBottom: '20px',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  actionButton: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '5px 10px',
    marginRight: '5px',
    border: 'none',
    borderRadius: '3px',
    cursor: 'pointer',
  },
  deleteButton: {
    backgroundColor: '#dc3545',
    color: 'white',
    padding: '5px 10px',
    border: 'none',
    borderRadius: '3px',
    cursor: 'pointer',
  },
};

export default Movies;
