import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Customers = () => {
  const [customers, setCustomers] = useState([]);
  const [formData, setFormData] = useState({
    customer_id: '',
    customer_name: '',
    phone_number: '',
    membership_id: ''
  });
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    fetchCustomers();
  }, []);

  const fetchCustomers = () => {
    axios.get('/api/customers')
      .then(response => {
        setCustomers(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the customers data!", error);
      });
  };

  const handleDelete = (id) => {
    axios.delete(`/api/customers/${id}`)
      .then(response => {
        setCustomers(customers.filter(customer => customer.customer_id !== id));
      })
      .catch(error => {
        console.error("There was an error deleting the customer!", error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      axios.put(`/api/customers/${formData.customer_id}`, formData)
        .then(response => {
          fetchCustomers(); // Refresh the customers list
          resetForm();
        })
        .catch(error => {
          console.error("There was an error updating the customer!", error);
        });
    } else {
      axios.post('/api/customers', formData)
        .then(response => {
          fetchCustomers(); // Refresh the customers list
          resetForm();
        })
        .catch(error => {
          console.error("There was an error creating the customer!", error);
        });
    }
  };

  const handleEdit = (customer) => {
    setFormData(customer);
    setIsEditing(true);
  };

  const resetForm = () => {
    setFormData({
      customer_id: '',
      customer_name: '',
      phone_number: '',
      membership_id: ''
    });
    setIsEditing(false);
  };

  return (
    <div style={styles.container}>
      <h1>Customers List</h1>
      <form onSubmit={handleFormSubmit} style={styles.form}>
        <input
          type="text"
          name="customer_name"
          placeholder="Customer Name"
          value={formData.customer_name}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="phone_number"
          placeholder="Phone Number"
          value={formData.phone_number}
          onChange={handleInputChange}
          required
        />
        <input
          type="number"
          name="membership_id"
          placeholder="Membership ID"
          value={formData.membership_id}
          onChange={handleInputChange}
          required
        />
        <button type="submit" style={styles.actionButton}>
          {isEditing ? 'Update Customer' : 'Create Customer'}
        </button>
        {isEditing && <button type="button" onClick={resetForm} style={styles.resetButton}>Cancel</button>}
      </form>
      <table style={styles.table}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Phone</th>
            <th>Membership</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {customers.map((customer) => (
            <tr key={customer.customer_id}>
              <td>{customer.customer_id}</td>
              <td>{customer.customer_name}</td>
              <td>{customer.phone_number}</td>
              <td>{customer.membership_id}</td>
              <td>
                <button onClick={() => handleEdit(customer)} style={styles.actionButton}>Edit</button>
                <button onClick={() => handleDelete(customer.customer_id)} style={styles.deleteButton}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
  },
  form: {
    marginBottom: '20px',
  },
  createButton: {
    backgroundColor: 'green',
    color: 'white',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    marginBottom: '20px',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  actionButton: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '5px 10px',
    marginRight: '5px',
    border: 'none',
    borderRadius: '3px',
    cursor: 'pointer',
  },
  deleteButton: {
    backgroundColor: '#dc3545',
    color: 'white',
    padding: '5px 10px',
    border: 'none',
    borderRadius: '3px',
    cursor: 'pointer',
  },
  resetButton: {
    backgroundColor: '#6c757d',
    color: 'white',
    padding: '5px 10px',
    border: 'none',
    borderRadius: '3px',
    cursor: 'pointer',
    marginLeft: '10px',
  },
};

export default Customers;
