import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Customers = () => {
  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    axios.get('/api/customers')
      .then(response => {
        console.log(response.data);  // Check what data you're getting
        setCustomers(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the customers data!", error);
      });
  }, []);

  const handleDelete = (id) => {
    axios.delete(`/api/customers/${id}`)
      .then(response => {
        setCustomers(customers.filter(customer => customer.customer_id !== id));
      })
      .catch(error => {
        console.error("There was an error deleting the customer!", error);
      });
  };

  return (
    <div style={styles.container}>
      <h1>Customers List</h1>
      <Link to="/create-customer" style={styles.createButton}>Create +</Link>
      <table style={styles.table}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Phone</th>
            <th>Membership</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {customers.map((customer) => (
            <tr key={customer.customer_id}>
              <td>{customer.customer_id}</td>
              <td>{customer.customer_name}</td>
              <td>{customer.phone_number}</td>
              <td>{customer.membership_id}</td>
              <td>
                <Link to={`/customers/${customer.customer_id}`} style={styles.actionButton}>Read</Link>
                <Link to={`/customers/edit/${customer.customer_id}`} style={styles.actionButton}>Edit</Link>
                <button style={styles.deleteButton} onClick={() => handleDelete(customer.customer_id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
  },
  createButton: {
    backgroundColor: 'green',
    color: 'white',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    marginBottom: '20px',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  actionButton: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '5px 10px',
    marginRight: '5px',
    border: 'none',
    borderRadius: '3px',
    cursor: 'pointer',
  },
  deleteButton: {
    backgroundColor: '#dc3545',
    color: 'white',
    padding: '5px 10px',
    border: 'none',
    borderRadius: '3px',
    cursor: 'pointer',
  },
};

export default Customers;
