import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';  // Import Link for navigation
import axios from 'axios';
import '../styles/style.css';  // Import the CSS file

const Movies = () => {
  const [movies, setMovies] = useState([]);
  const [formData, setFormData] = useState({
    movie_id: '',
    title: '',
    genre: '',
    release_date: '',
    price: '',
    amount: ''
  });
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = () => {
    axios.get('http://flip1.engr.oregonstate.edu:3002/api/movies')
      .then(response => {
        setMovies(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the movies data!", error);
      });
  };

  const handleDelete = (id) => {
    axios.delete(`/api/movies/${id}`)
      .then(() => {
        setMovies(movies.filter(movie => movie.movie_id !== id));
      })
      .catch(error => {
        console.error("There was an error deleting the movie!", error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      axios.put(`/api/movies/${formData.movie_id}`, formData)
        .then(() => {
          fetchMovies(); // Refresh the movies list
          resetForm();
        })
        .catch(error => {
          console.error("There was an error updating the movie!", error);
        });
    } else {
      axios.post('/api/movies', formData)
        .then(() => {
          fetchMovies(); // Refresh the movies list
          resetForm();
        })
        .catch(error => {
          console.error("There was an error creating the movie!", error);
        });
    }
  };

  const handleEdit = (movie) => {
    setFormData(movie);
    setIsEditing(true);
  };

  const resetForm = () => {
    setFormData({
      movie_id: '',
      title: '',
      genre: '',
      release_date: '',
      price: '',
      amount: ''
    });
    setIsEditing(false);
  };

  return (
    <div className="container">
      {/* Navigation Links */}
      <nav className="nav">
        <Link to="/" className="nav-link">Home</Link>
        <Link to="/customers" className="nav-link">Customers</Link>
        <Link to="/memberships" className="nav-link">Memberships</Link>
        <Link to="/movies" className="nav-link">Movies</Link>
        <Link to="/payments" className="nav-link">Payments</Link>
        <Link to="/rentals" className="nav-link">Rentals</Link>
      </nav>

      <h1>Movies List</h1>
      <form onSubmit={handleFormSubmit} className="form">
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={formData.title}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="genre"
          placeholder="Genre"
          value={formData.genre}
          onChange={handleInputChange}
          required
        />
        <input
          type="date"
          name="release_date"
          placeholder="Release Date"
          value={formData.release_date}
          onChange={handleInputChange}
          required
        />
        <input
          type="number"
          step="0.01"
          name="price"
          placeholder="Price"
          value={formData.price}
          onChange={handleInputChange}
          required
        />
        <input
          type="number"
          name="amount"
          placeholder="Amount"
          value={formData.amount}
          onChange={handleInputChange}
          required
        />
        <button type="submit" className="button actionButton">
          {isEditing ? 'Update Movie' : 'Create Movie'}
        </button>
        {isEditing && <button type="button" onClick={resetForm} className="button resetButton">Cancel</button>}
      </form>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Genre</th>
            <th>Release Date</th>
            <th>Price</th>
            <th>Amount</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {movies.map((movie) => (
            <tr key={movie.movie_id}>
              <td>{movie.movie_id}</td>
              <td>{movie.title}</td>
              <td>{movie.genre}</td>
              <td>{movie.release_date}</td>
              <td>{movie.price}</td>
              <td>{movie.amount}</td>
              <td>
                <button onClick={() => handleEdit(movie)} className="button actionButton">Edit</button>
                <button onClick={() => handleDelete(movie.movie_id)} className="button deleteButton">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Movies;
