import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Rentals = () => {
  const [rentals, setRentals] = useState([]);
  const [formData, setFormData] = useState({
    rental_id: '',
    movie_id: '',
    customer_id: '',
    start_date: '',
    due_date: '',
    date_returned: ''
  });
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    fetchRentals();
  }, []);

  const fetchRentals = () => {
    axios.get('/api/rentals')
      .then(response => {
        setRentals(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the rentals data!", error);
      });
  };

  const handleDelete = (id) => {
    axios.delete(`/api/rentals/${id}`)
      .then(() => {
        setRentals(rentals.filter(rental => rental.rental_id !== id));
      })
      .catch(error => {
        console.error("There was an error deleting the rental!", error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      axios.put(`/api/rentals/${formData.rental_id}`, formData)
        .then(() => {
          fetchRentals(); // Refresh the rentals list
          resetForm();
        })
        .catch(error => {
          console.error("There was an error updating the rental!", error);
        });
    } else {
      axios.post('/api/rentals', formData)
        .then(() => {
          fetchRentals(); // Refresh the rentals list
          resetForm();
        })
        .catch(error => {
          console.error("There was an error creating the rental!", error);
        });
    }
  };

  const handleEdit = (rental) => {
    setFormData(rental);
    setIsEditing(true);
  };

  const resetForm = () => {
    setFormData({
      rental_id: '',
      movie_id: '',
      customer_id: '',
      start_date: '',
      due_date: '',
      date_returned: ''
    });
    setIsEditing(false);
  };

  return (
    <div style={styles.container}>
      <h1>Rentals List</h1>
      <form onSubmit={handleFormSubmit} style={styles.form}>
        <input
          type="number"
          name="movie_id"
          placeholder="Movie ID"
          value={formData.movie_id}
          onChange={handleInputChange}
          required
        />
        <input
          type="number"
          name="customer_id"
          placeholder="Customer ID"
          value={formData.customer_id}
          onChange={handleInputChange}
          required
        />
        <input
          type="date"
          name="start_date"
          placeholder="Start Date"
          value={formData.start_date}
          onChange={handleInputChange}
          required
        />
        <input
          type="date"
          name="due_date"
          placeholder="Due Date"
          value={formData.due_date}
          onChange={handleInputChange}
          required
        />
        <input
          type="date"
          name="date_returned"
          placeholder="Date Returned"
          value={formData.date_returned}
          onChange={handleInputChange}
        />
        <button type="submit" style={styles.actionButton}>
          {isEditing ? 'Update Rental' : 'Create Rental'}
        </button>
        {isEditing && <button type="button" onClick={resetForm} style={styles.resetButton}>Cancel</button>}
      </form>
      <table style={styles.table}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Movie ID</th>
            <th>Customer ID</th>
            <th>Start Date</th>
            <th>Due Date</th>
            <th>Returned</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {rentals.map((rental) => (
            <tr key={rental.rental_id}>
              <td>{rental.rental_id}</td>
              <td>{rental.movie_id}</td>
              <td>{rental.customer_id}</td>
              <td>{rental.start_date}</td>
              <td>{rental.due_date}</td>
              <td>{rental.date_returned ? rental.date_returned : 'Not Returned'}</td>
              <td>
                <button onClick={() => handleEdit(rental)} style={styles.actionButton}>Edit</button>
                <button onClick={() => handleDelete(rental.rental_id)} style={styles.deleteButton}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
  },
  form: {
    marginBottom: '20px',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  actionButton: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '5px 10px',
    marginRight: '5px',
    border: 'none',
    borderRadius: '3px',
    cursor: 'pointer',
  },
  deleteButton: {
    backgroundColor: '#dc3545',
    color: 'white',
    padding: '5px 10px',
    border: 'none',
    borderRadius: '3px',
    cursor: 'pointer',
  },
  resetButton: {
    backgroundColor: '#6c757d',
    color: 'white',
    padding: '5px 10px',
    border: 'none',
    borderRadius: '3px',
    cursor: 'pointer',
    marginLeft: '10px',
  },
};

export default Rentals;
