import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Payments = () => {
  const [payments, setPayments] = useState([]);

  // Fetch data from the backend when the component mounts
  useEffect(() => {
    axios.get('/api/payments')  // Replace with your actual backend API endpoint
      .then(response => {
        setPayments(response.data);  // Assume the response is an array of payments
      })
      .catch(error => {
        console.error("There was an error fetching the payments data!", error);
      });
  }, []);

  const handleDelete = (id) => {
    axios.delete(`/api/payments/${id}`)
      .then(response => {
        // Remove the deleted payment from the state
        setPayments(payments.filter(payment => payment.id !== id));
      })
      .catch(error => {
        console.error("There was an error deleting the payment!", error);
      });
  };

  return (
    <div style={styles.container}>
      <h1>Payments List</h1>
      <Link to="/create-payment" style={styles.createButton}>Create +</Link>
      <table style={styles.table}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Amount</th>
            <th>Date</th>
            <th>Method</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {payments.map((payment) => (
            <tr key={payment.id}>
              <td>{payment.id}</td>
              <td>{payment.amount}</td>
              <td>{payment.date}</td>
              <td>{payment.method}</td>
              <td>{payment.status}</td>
              <td>
                <Link to={`/payments/${payment.id}`} style={styles.actionButton}>Read</Link>
                <Link to={`/payments/edit/${payment.id}`} style={styles.actionButton}>Edit</Link>
                <button style={styles.deleteButton} onClick={() => handleDelete(payment.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles = {
  // styles remain the same
};

export default Payments;
