import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Rentals = () => {
  const [rentals, setRentals] = useState([]);

  useEffect(() => {
    axios.get('/api/rentals')
      .then(response => {
        setRentals(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the rentals data!", error);
      });
  }, []);

  const handleDelete = (id) => {
    axios.delete(`/api/rentals/${id}`)
      .then(response => {
        setRentals(rentals.filter(rental => rental.id !== id));
      })
      .catch(error => {
        console.error("There was an error deleting the rental!", error);
      });
  };

  return (
    <div style={styles.container}>
      <h1>Rentals List</h1>
      <Link to="/create-rental" style={styles.createButton}>Create +</Link>
      <table style={styles.table}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Movie</th>
            <th>Customer</th>
            <th>Start Date</th>
            <th>Due Date</th>
            <th>Returned</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {rentals.map((rental) => (
            <tr key={rental.id}>
              <td>{rental.id}</td>
              <td>{rental.movie}</td>
              <td>{rental.customer}</td>
              <td>{rental.startDate}</td>
              <td>{rental.dueDate}</td>
              <td>{rental.returned ? rental.returned : 'Not Returned'}</td>
              <td>
                <Link to={`/rentals/${rental.id}`} style={styles.actionButton}>Read</Link>
                <Link to={`/rentals/edit/${rental.id}`} style={styles.actionButton}>Edit</Link>
                <button style={styles.deleteButton} onClick={() => handleDelete(rental.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
  },
  createButton: {
    backgroundColor: 'green',
    color: 'white',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    marginBottom: '20px',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  actionButton: {
    backgroundColor: '#007bff',
    color: 'white',
    padding: '5px 10px',
    marginRight: '5px',
    border: 'none',
    borderRadius: '3px',
    cursor: 'pointer',
  },
  deleteButton: {
    backgroundColor: '#dc3545',
    color: 'white',
    padding: '5px 10px',
    border: 'none',
    borderRadius: '3px',
    cursor: 'pointer',
  },
};

export default Movies;
