import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/style.css';  // Import the CSS file

const Payments = () => {
  const [payments, setPayments] = useState([]);
  const [formData, setFormData] = useState({
    payment_id: '',
    payment_amount: '',
    payment_date: '',
    payment_method: '',
    payment_status: ''
  });
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    fetchPayments();
  }, []);

  const fetchPayments = () => {
    axios.get('/api/payments')
      .then(response => {
        setPayments(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the payments data!", error);
      });
  };

  const handleDelete = (id) => {
    axios.delete(`/api/payments/${id}`)
      .then(() => {
        setPayments(payments.filter(payment => payment.payment_id !== id));
      })
      .catch(error => {
        console.error("There was an error deleting the payment!", error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      axios.put(`/api/payments/${formData.payment_id}`, formData)
        .then(() => {
          fetchPayments(); // Refresh the payments list
          resetForm();
        })
        .catch(error => {
          console.error("There was an error updating the payment!", error);
        });
    } else {
      axios.post('/api/payments', formData)
        .then(() => {
          fetchPayments(); // Refresh the payments list
          resetForm();
        })
        .catch(error => {
          console.error("There was an error creating the payment!", error);
        });
    }
  };

  const handleEdit = (payment) => {
    setFormData(payment);
    setIsEditing(true);
  };

  const resetForm = () => {
    setFormData({
      payment_id: '',
      payment_amount: '',
      payment_date: '',
      payment_method: '',
      payment_status: ''
    });
    setIsEditing(false);
  };

  return (
    <div className="container">
      <h1>Payments List</h1>
      <form onSubmit={handleFormSubmit} className="form">
        <input
          type="number"
          step="0.01"
          name="payment_amount"
          placeholder="Amount"
          value={formData.payment_amount}
          onChange={handleInputChange}
          required
        />
        <input
          type="date"
          name="payment_date"
          placeholder="Date"
          value={formData.payment_date}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="payment_method"
          placeholder="Method"
          value={formData.payment_method}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="payment_status"
          placeholder="Status"
          value={formData.payment_status}
          onChange={handleInputChange}
          required
        />
        <button type="submit" className="button actionButton">
          {isEditing ? 'Update Payment' : 'Create Payment'}
        </button>
        {isEditing && <button type="button" onClick={resetForm} className="button resetButton">Cancel</button>}
      </form>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Amount</th>
            <th>Date</th>
            <th>Method</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {payments.map((payment) => (
            <tr key={payment.payment_id}>
              <td>{payment.payment_id}</td>
              <td>{payment.payment_amount}</td>
              <td>{payment.payment_date}</td>
              <td>{payment.payment_method}</td>
              <td>{payment.payment_status}</td>
              <td>
                <button onClick={() => handleEdit(payment)} className="button actionButton">Edit</button>
                <button onClick={() => handleDelete(payment.payment_id)} className="button deleteButton">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Payments;
