import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Memberships = () => {
  const [memberships, setMemberships] = useState([]);

  useEffect(() => {
    axios.get('/api/memberships')
      .then(response => {
        setMemberships(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the memberships data!", error);
      });
  }, []);

  const handleDelete = (id) => {
    axios.delete(`/api/memberships/${id}`)
      .then(response => {
        setMemberships(memberships.filter(membership => membership.id !== id));
      })
      .catch(error => {
        console.error("There was an error deleting the membership!", error);
      });
  };

  return (
    <div style={styles.container}>
      <h1>Memberships List</h1>
      <Link to="/create-membership" style={styles.createButton}>Create +</Link>
      <table style={styles.table}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Type</th>
            <th>Discount</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {memberships.map((membership) => (
            <tr key={membership.id}>
              <td>{membership.id}</td>
              <td>{membership.type}</td>
              <td>{membership.discount}</td>
              <td>
                <Link to={`/memberships/${membership.id}`} style={styles.actionButton}>Read</Link>
                <Link to={`/memberships/edit/${membership.id}`} style={styles.actionButton}>Edit</Link>
                <button style={styles.deleteButton} onClick={() => handleDelete(membership.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles = {
  // styles remain the same
};

export default Memberships;
